"use strict";var link=document.getElementById("back-to-top"),amountScrolled=150;function addClass(s,l){s.classList?s.classList.add(l):s.className+=" "+l}function removeClass(s,l){s.classList?s.classList.remove(l):s.className=s.className.replace(new RegExp("(^|\\b)"+l.split(" ").join("|")+"(\\b|$)","gi")," ")}window.addEventListener("scroll",function(s){window.scrollY>amountScrolled?addClass(link,"show"):removeClass(link,"show")});